import React from "react";

const Sidebar = ({ chats, activeChatId, onNewChat, onSelectChat, onDeleteChat }) => {
  return (
    <div className="w-64 h-screen p-4 bg-slate-900/70 backdrop-blur border-r border-slate-700 flex flex-col">

      <button
        onClick={onNewChat}
        className="mb-4 py-2 rounded-lg bg-slate-800 hover:bg-slate-700 transition"
      >
        ‚ûï New Chat
      </button>

      <div className="flex-1 space-y-2 overflow-y-auto">
        {chats.map(chat => (
          <div
            key={chat.id}
            className={`flex justify-between items-center px-3 py-2 rounded-lg cursor-pointer
              ${chat.id === activeChatId ? "bg-slate-700" : "hover:bg-slate-800"}`}
          >
            <span onClick={() => onSelectChat(chat.id)} className="truncate">
              {chat.title}
            </span>

            <button
              onClick={() => onDeleteChat(chat.id)}
              className="text-red-400 hover:text-red-300"
            >
              üóëÔ∏è
            </button>
          </div>
        ))}
      </div>

    </div>
  );
};

export default Sidebar;
