import React, { useState } from "react";
import FileUpload from "./FileUpload";

const ChatInput = ({ onNewMessage }) => {
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);

  const submit = async () => {
    if (!text.trim()) return;

    // 1️⃣ UI তে user message দেখাও
    onNewMessage({ sender: "user", text });

    setLoading(true);
    setText("");

    // 2️⃣ BACKEND CALL
    const res = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded"
      },
      body: new URLSearchParams({ query: text })
    });

    const data = await res.json();

    // 3️⃣ LLM response UI তে দেখাও
    onNewMessage({ sender: "assistant", text: data.answer });

    setLoading(false);
  };

  return (
    <div className="mt-3 space-y-2">
      <FileUpload />

      <div className="flex gap-2">
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Ask something..."
          className="flex-1 px-4 py-2 rounded-lg bg-slate-800 outline-none"
          onKeyDown={(e) => e.key === "Enter" && submit()}
        />

        <button
          onClick={submit}
          className="px-4 rounded-lg bg-slate-700 hover:bg-slate-600"
          disabled={loading}
        >
          {loading ? "Thinking..." : "Send"}
        </button>
      </div>
    </div>
  );
};

export default ChatInput;
