import React, { useState } from "react";
import Sidebar from "./components/Sidebar";
import ChatWindow from "./components/ChatWindow";
import ChatInput from "./components/ChatInput";

const newChat = () => ({
  id: Date.now(),
  title: "New Chat",
  messages: [{ sender: "assistant", text: "Hello ðŸ‘‹ How can I help?" }],
  files: []
});

const App = () => {
  const [chats, setChats] = useState([newChat()]);
  const [activeId, setActiveId] = useState(chats[0].id);

  const activeChat = chats.find(c => c.id === activeId);

  const send = (text) => {
    setChats(prev =>
      prev.map(c =>
        c.id === activeId
          ? {
              ...c,
              title: c.title === "New Chat" ? text.slice(0, 20) : c.title,
              messages: [...c.messages, { sender: "user", text }]
            }
          : c
      )
    );
  };

  const uploadFiles = (files) => {
    setChats(prev =>
      prev.map(c =>
        c.id === activeId ? { ...c, files: [...c.files, ...files] } : c
      )
    );
  };

  const deleteChat = (id) => {
    const remaining = chats.filter(c => c.id !== id);
    setChats(remaining);
    if (id === activeId && remaining.length)
      setActiveId(remaining[0].id);
  };

  return (
    <div className="flex h-screen">
      <Sidebar
        chats={chats}
        activeChatId={activeId}
        onNewChat={() => {
          const c = newChat();
          setChats([c, ...chats]);
          setActiveId(c.id);
        }}
        onSelectChat={setActiveId}
        onDeleteChat={deleteChat}
      />

      <div className="flex-1 p-6">
        <ChatWindow messages={activeChat.messages} />
        <ChatInput onSend={send} onUpload={uploadFiles} />
      </div>
    </div>
  );
};

export default App;
