import React, { useEffect, useRef } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import "./chatAnimations.css"; // We'll create this

const ChatWindow = ({ messages }) => {
  const chatEndRef = useRef(null);

  // Auto-scroll on new message
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="flex flex-col p-4 h-[70vh] overflow-y-auto border rounded shadow bg-white">
      <TransitionGroup>
        {messages.map((msg, idx) => (
          <CSSTransition key={idx} timeout={300} classNames="fade">
            <div className={`my-2 flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className={`${msg.sender === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-900'} p-3 rounded-lg max-w-[70%] shadow`}>
                {msg.text}
              </div>
            </div>
          </CSSTransition>
        ))}
      </TransitionGroup>
      <div ref={chatEndRef} />
    </div>
  );
};

export default ChatWindow;
