import React, { useState } from "react";
import ChatWindow from "./components/ ChatWindow";
import ChatInput from "./components/ ChatInput";
import Sidebar from "./components/ Sidebar";

const createNewChat = () => ({
  id: Date.now(),
  title: "New Chat",
  messages: [
    { sender: "assistant", text: "âš¡ AI System Online. Enter command." }
  ]
});

const App = () => {
  const [chats, setChats] = useState([createNewChat()]);
  const [activeChatId, setActiveChatId] = useState(chats[0].id);
  const [typing, setTyping] = useState(false);

  const activeChat = chats.find(c => c.id === activeChatId);

  const handleNewChat = () => {
    const newChat = createNewChat();
    setChats(prev => [newChat, ...prev]);
    setActiveChatId(newChat.id);
  };

  const handleSend = (text) => {
    setChats(prev =>
      prev.map(chat =>
        chat.id === activeChatId
          ? {
              ...chat,
              title: chat.title === "New Chat" ? text.slice(0, 20) : chat.title,
              messages: [...chat.messages, { sender: "user", text }]
            }
          : chat
      )
    );

    setTyping(true);

    setTimeout(() => {
      setChats(prev =>
        prev.map(chat =>
          chat.id === activeChatId
            ? {
                ...chat,
                messages: [
                  ...chat.messages,
                  { sender: "assistant", text: "ðŸ¤– Processing response..." }
                ]
              }
            : chat
        )
      );
      setTyping(false);
    }, 1000);
  };

  return (
    <div className="min-h-screen flex bg-black text-white">
      
      {/* Sidebar */}
      <Sidebar
        chats={chats}
        activeChatId={activeChatId}
        onNewChat={handleNewChat}
        onSelectChat={setActiveChatId}
      />

      {/* Chat Area */}
      <div className="flex-1 flex justify-center items-center">
        <div className="
          w-full max-w-4xl p-4
          border border-cyan-500 rounded-xl
          shadow-[0_0_35px_#22d3ee]
          bg-black/60
        ">
          <h1 className="text-lg mb-3 text-cyan-400">
            ðŸŽ® AI GAMING CHAT
          </h1>

          <ChatWindow
            messages={activeChat.messages}
            typing={typing}
          />

          <ChatInput onSend={handleSend} />
        </div>
      </div>

    </div>
  );
};

export default App;
