import React, { useState } from "react";
import ChatWindow from "./components/ ChatWindow";
import ChatInput from "./components/ ChatInput";

const App = () => {
  const [messages, setMessages] = useState([
    { sender: "assistant", text: "Welcome! Ask me anything or upload a file." }
  ]);

  const handleSend = (text) => {
    setMessages([...messages, { sender: "user", text }]);

    // TODO: Call backend API here
    // Example placeholder response
    setTimeout(() => {
      setMessages(prev => [...prev, { sender: "assistant", text: "This is a simulated answer from the assistant." }]);
    }, 500);
  };

  return (
    <div className="max-w-3xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Business Chat Assistant</h1>
      <ChatWindow messages={messages} />
      <ChatInput onSend={handleSend} />
    </div>
  );
};
const [typing, setTyping] = useState(false);

const handleSend = (text) => {
  setMessages([...messages, { sender: "user", text }]);
  setTyping(true);

  setTimeout(() => {
    setMessages(prev => [...prev, { sender: "assistant", text: "This is a simulated answer from the assistant." }]);
    setTyping(false);
  }, 1000);
};

export default App;
