import React, { useState } from "react";
import ChatWindow from "./components/ ChatWindow";
import ChatInput from "./components/ ChatInput";

const App = () => {
  const [messages, setMessages] = useState([
    { sender: "assistant", text: "âš¡ AI System Online. Enter command." }
  ]);

  const [typing, setTyping] = useState(false);

  const handleSend = (text) => {
    setMessages(prev => [...prev, { sender: "user", text }]);
    setTyping(true);

    // TEMP LLM SIMULATION
    setTimeout(() => {
      setMessages(prev => [
        ...prev,
        { sender: "assistant", text: "ðŸ¤– Command received. Processing response..." }
      ]);
      setTyping(false);
    }, 1000);
  };

  return (
    <div className="min-h-screen flex justify-center items-center">
      <div className="
        w-full max-w-4xl p-4
        border border-cyan-500 rounded-xl
        shadow-[0_0_35px_#22d3ee]
        bg-black/60
      ">
        <h1 className="text-xl mb-4 text-cyan-400">
          ðŸŽ® AI GAMING CHAT INTERFACE
        </h1>

        <ChatWindow messages={messages} typing={typing} />
        <ChatInput onSend={handleSend} />
      </div>
    </div>
  );
};

export default App;
