import React from "react";

const ChatWindow = ({ messages }) => {
  return (
    <div className="flex flex-col p-4 h-[70vh] overflow-y-auto border rounded shadow bg-white">
      {messages.map((msg, idx) => (
        <div key={idx} className={`my-2 flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
          <div className={`${msg.sender === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-900'} p-3 rounded-lg max-w-[70%]`}>
            {msg.text}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ChatWindow;
